mapper.insert:
INSERT INTO user1 (
			user_seq
			, user_id
			, user_name
			, user_pwd
			, reg_date
			      )
	VALUES (
			#{userSeq}
			, #{userId}
			, #{userName}
			, #{userPwd}
			, SYSDATE 
		    )

INSERT INTO
	 user_history (
	 			   user_seq
	 			   , birth
	 			   , email
	 			   , tel
	 			   )
		VALUES (
				#{userSeq}
				,TO_DATE(#{birth}, 'YYYY-MM-DD')
				, #{email}
				, #{tel}
				)

mapper.update:
		INSERT ALL
        INTO user1 (
        			user_seq
        			, user_id
        			, user_name
        			, user_pwd
        			, reg_date
        			)
            VALUES (
            		#{userSeq}
            		, #{userId}
            		, #{userName}
            		, #{userPwd}
            		, SYSDATE
            		)
        INTO user_history (
        			user_seq
        			, birth
        			, email
        			, tel
        				  )
            VALUES (
            		#{userSeq}
            		, TO_DATE(#{birth}, 'YYYY-MM-DD')
            		, #{email}
            		, #{tel}
            		)
    	SELECT * FROM DUAL

UPDATE user1 
   SET user_name = #{userName}
   	   , user_pwd = #{userPwd}
WHERE  user_id = #{userId}

UPDATE user_history
   SET birth = TO_DATE(#{birth}, 'YYYY-MM-DD')
   	   , email = #{email}
   	   , tel = #{tel}
WHERE user_seq = #{userSeq}
